INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b4ac538b-6ee8-498b-9918-27a880a388a0', N'ecbce90b-d6a1-406a-9a45-be51fd4022e0', N'013101', N'0131', N'气血亏虚', 1, N'', N'产后乳汁自出，量少质清稀，乳房柔软无胀满。面色少华，神疲气短，或动则心悸。舌淡苔薄，脉细弱。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'96d34905-afc0-46f7-8be4-b83b778773ef', N'ecbce90b-d6a1-406a-9a45-be51fd4022e0', N'013102', N'0131', N'肝郁化火', 1, N'', N'产后乳汁自出，质较稠，乳房胀痛。精神仰郁或急躁易怒，甚或心烦少寐，口苦咽干，便秘尿黄。舌质红，苔薄黄，脉弦数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'94230653-f048-4bc0-becd-b988551c5388', N'1ed54efd-c53d-4514-aa8f-7ed7fb06a27b', N'013201', N'0132', N'脾虚气陷', 1, N'', N'子宫脱垂，劳则加剧，卧则消失，小腹坠胀，面色少华，四肢乏力，少语懒言，带下色白，量多质稀。舌淡，苔薄，脉细弱。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'22bf89f7-7ad7-4f1e-94e5-e06f0dc54da4', N'1ed54efd-c53d-4514-aa8f-7ed7fb06a27b', N'013202', N'0132', N'肾阳亏虚', 1, N'', N'子宫脱垂，腰酸腿软，小腹下垂，头晕耳鸣，小便频数，夜间尤甚。舌淡红，脉沉弱。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
