INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7e00ae29-b74d-420c-b4ca-4a2c49a2e6ff', N'90b9fc26-8181-4b63-ba42-891943413edb', N'013301', N'0133', N'肾阳亏虚', 1, N'', N'婚后不孕，经行量少色淡，头晕耳鸣，腰酸形寒，小腹冷感，带下清稀，性欲淡漠，有时便溏。舌淡胖，苔白，脉沉细尺弱。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
