INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b0ae3192-8e4b-4e43-8f12-59dbb3683049', N'90b9fc26-8181-4b63-ba42-891943413edb', N'013304', N'0133', N'肝气郁滞', 1, N'', N'婚后不孕，月经不调，量或多或少，色紫红有血块，情志失畅，经前胸闷急躁，乳房作胀，行经少腹疼痛。苔薄黄，脉弦。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
