INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e0a9129b-c7b8-449b-8804-25ae950484d8', N'7f06b32b-f70b-4134-9318-b788ac9ebd4b', N'030403', N'0304', N'气滞血瘀', 1, N'', N'肛门刺痛，便时便后尤甚。肛门紧缩，裂口色紫暗。占质紫暗，脉弦或涩。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
