INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1de10193-32f1-49d2-a48b-2fb3ab9bbbfb', N'096dc779-dd53-4916-8fa4-cf3a94b8cc62', N'030502', N'0305', N'热毒炽盛', 1, N'', N'肛门肿痛剧烈，可持续数日，痛如鸡啄，夜寐不安，伴有恶寒发热，口干便秘，小便困难。肛周红肿，按之有波动感或穿刺有脓。舌红，苔黄，脉弦滑。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'41bbf078-ca86-4c29-84c4-a34977981fc6', N'096dc779-dd53-4916-8fa4-cf3a94b8cc62', N'030503', N'0305', N'阴虚毒恋', 1, N'', N'肛门肿痛、灼热，表皮色红，溃后难敛，伴有午后潮热，心烦口干，夜间盗汗。舌红，少苔，脉细数。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd8afddea-2190-448d-8585-d2ba6cf1bee1', N'95617344-0f74-4eb5-ae09-d8e8fbb9522c', N'030601', N'0306', N'湿热下注', 1, N'', N'肛周经常流脓液，脓质稠厚，肛门胀痛，局部灼热。肛周有溃口，按之有索状物通向肛内。舌红，苔黄，脉弦或滑。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'be416859-45aa-49ea-9276-f470e2d96b0c', N'95617344-0f74-4eb5-ae09-d8e8fbb9522c', N'030602', N'0306', N'正虚邪恋', 1, N'', N'肛周流脓液，质地稀薄，肛门隐隐作痛，外口皮色暗淡，漏口时溃时愈，肛周有溃口，按之较硬，或有脓液从溃口流出，且多有索状物通向肛内，可伴有神疲乏力。舌淡，苔薄，脉濡。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f8b10dfe-0b77-405b-b01c-64203271cc52', N'95617344-0f74-4eb5-ae09-d8e8fbb9522c', N'030603', N'0306', N'阴液亏虚', 1, N'', N'肛周有溃口，颜色淡红，按之有索状物通向肛内，可伴有潮热盗汗，心烦口干。舌红，少苔，脉细数。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'46b1cdb9-95ea-4445-92fb-5344eaca645a', N'fcc703eb-1a63-40e4-9854-6b9ee8f88bf9', N'030701', N'0307', N'脾虚气陷', 1, N'', N'便时肛内肿物脱出，轻重不一，色淡红，伴有肛门坠胀，大便带血，神疲乏力，食欲不振，甚则有头昏耳鸣，腰膝酸软。舌淡，苔薄白，脉弱。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'05f586ec-408a-454b-8aca-b57f24fcab02', N'fcc703eb-1a63-40e4-9854-6b9ee8f88bf9', N'030702', N'0307', N'湿热下注', 1, N'', N'肛内肿物脱出，色紫暗或深红，甚则表面部分溃破，糜烂，肛门坠痛，肛内指检有灼热感。舌红，苔黄腻，脉弦数。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ee64cddc-aade-4968-ac68-b7260aef560c', N'f45ae0b0-a86c-4b11-92b2-fd0d323a4b6d', N'030801', N'0308', N'湿热下注', 1, N'', N'肛周潮湿、潮红、有灼热感。肥大的肛乳头充血、水肿。舌红，苔黄，脉滑数。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4d08dcbc-c043-48c8-baa5-d453e0ee2c73', N'f45ae0b0-a86c-4b11-92b2-fd0d323a4b6d', N'030802', N'0308', N'气滞血瘀', 1, N'', N'排便后肛门部肿物脱出，表面色紫暗，伴有肛门坠胀。舌紫暗，苔薄，脉涩。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9237319c-f50f-4382-aa24-f5a09420de24', N'16f0ef9c-e5c5-41a1-94c1-8276ba925cf0', N'030901', N'0309', N'肾气亏虚', 1, N'', N'肛门坠胀，腰骶酸软，乏力，毛发不荣，肿物溃后流液清稀，溃口凹陷不愈。舌淡，苔薄，脉细弱。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'02be2c46-5a07-4723-a470-67a039595b34', N'16f0ef9c-e5c5-41a1-94c1-8276ba925cf0', N'030902', N'0309', N'湿热下注', 1, N'', N'肛门胀痛，局部红肿，按之可有波动感，伴有恶寒发热。舌红，苔黄腻，脉数。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
