INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0428e22b-daa1-408c-b7b0-546881589e58', N'4a84d33f-527d-4f04-b02f-9c5ee3eafd03', N'031102', N'0311', N'气滞血瘀', 1, N'', N'肿物脱出肛外，不能回纳，疼痛甚，表面紫暗。舌紫，脉涩。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
