INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f128d449-76ad-4443-a894-26a885efd2e3', N'b2cf1c63-313d-4fe2-aed0-16e5747a38e3', N'031401', N'0314', N'脾气亏虚', 1, N'', N'神疲乏力，纳食欠佳，排便困难，伴肛门坠胀及便意不尽感，大便日行数次，质稀软，但解时困难。舌淡，苔薄，脉弱。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2655a061-40c2-4d7f-b117-f303e6279e90', N'b2cf1c63-313d-4fe2-aed0-16e5747a38e3', N'031402', N'0314', N'气阴两虚', 1, N'', N'头昏乏力，口干欲饮，排便困难，排便时间延长，便意未尽感，大便数日一行，质较硬。舌淡红，苔薄，脉弱。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd01dca99-8fe0-4065-be6f-70f3b967e5ce', N'abb6ff75-7a70-46a9-ad1f-baf3ac64143d', N'031501', N'0315', N'气滞血瘀', 1, N'', N'排便困难，排便时间延长，便时肛门疼痛，大便2一3日一行，质干。舌暗淡或夹有瘀斑，脉涩。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'dca7181f-088d-4c03-bcc7-b9f54fdc5abe', N'abb6ff75-7a70-46a9-ad1f-baf3ac64143d', N'031502', N'0315', N'湿热下注', 1, N'', N'排便困难，排便时间延长，大便3～4日一行，质干，便意不尽感，肛门坠胀。舌红，苔黄厚腻，脉滑数。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'47a7aa37-3022-415f-981d-f70f83c17621', N'abb6ff75-7a70-46a9-ad1f-baf3ac64143d', N'031503', N'0315', N'阴虚火旺', 1, N'', N'排便困难，排便时间延长，大便3～4日一行，质干结，口干欲饮，可有盗汗。舌红，苔少，脉细数、', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a6c47f4b-ca6e-4c9d-8aca-13b056bd3015', N'e4982182-71b4-4fd5-8b9a-fbf02d947f0d', N'031601', N'0316', N'湿热下注', 1, N'', N'以急性、亚急性湿疡较为多见。起病较急，皮损为潮红、肿胀、糜烂、滋水浸淫成片，结痂。伴有瘙痒或大便秘结，小便短黄，苔黄腻，脉滑数等症状者，为热重于湿。若起病较缓慢，皮损以丘疹、疱疹为主，滋水较多，伴有倦怠无力，纳呆，大便溏，苔白腻，脉滑等症状者，为湿重于热。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c7ff96a2-5e6e-45a9-bee8-594f5c8e95b2', N'e4982182-71b4-4fd5-8b9a-fbf02d947f0d', N'031602', N'0316', N'血虚风燥', 1, N'', N'以慢性湿疡为多见，反复发作，病程较长。皮损肥厚，呈苔藓样变，色索沉着，结痂脱屑等，或伴有头昏乏力，腰酸腿软。舌淡红，苔薄白，脉细无力。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8cfd5857-2f71-45a8-a459-c2c8a5116a84', N'22c16f97-f663-46e4-b52f-21713644876f', N'031701', N'0317', N'湿热蕴结', 1, N'', N'肛门坠胀，便次增多，大便带血，色泽暗红，或挟粘液，或有里急后重。舌红，苔黄腻，脉滑数。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'79978806-2c77-4f45-b344-fa6dbf1e3a0f', N'22c16f97-f663-46e4-b52f-21713644876f', N'031702', N'0317', N'气阴两虚', 1, N'', N'面色无华，消瘦乏力，便溏，或排便困难，便中带血，色泽紫暗，肛门坠胀，或伴心烦口干，夜间盗汗。舌红或绛，少苔，脉细弱或细数。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5e0d3e61-9bcc-4e52-b2cc-a3767f789f78', N'22c16f97-f663-46e4-b52f-21713644876f', N'031703', N'0317', N'气滞血瘀', 1, N'', N'肛周肿物隆起，触之坚硬如石，坠痛不休。或大便带血，色紫暗，里急后重，排便困难。舌质紫暗，脉涩。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3036196b-14a3-4690-b5d4-2e2a963aa615', N'0204b76e-0e41-4375-b449-efece3765811', N'040101', N'0401', N'暑湿热蕴', 1, N'', N'脓疱密集，色黄周围有红晕，糜烂面鲜红，多有口干，便干，小便黄。舌红，苔黄腻，脉濡滑数。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'44ade049-8ad5-4196-a366-ccc3fc0e520c', N'0204b76e-0e41-4375-b449-efece3765811', N'040102', N'0401', N'脾虚湿蕴', 1, N'', N'脓疱稀疏，色灰白或淡黄，糜烂面淡红。多有面黄，纳少，大便溏薄。舌淡，苔薄微腻，脉濡细。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2bd3f649-541f-4ad3-a88a-236c1e45910b', N'e763afd8-7fb2-4275-ab07-ae811ac6aef8', N'040201', N'0402', N'湿热瘀阻', 1, N'', N'颜面可见黄豆大小褐红色结节，四周浸润融合成片，或有溃疡。伴有纳差、烦躁易怒。舌淡红或边尖红，苔自腻或黄腻，脉弦滑。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0aea334e-6f8f-469a-b586-8032ac5b3450', N'e763afd8-7fb2-4275-ab07-ae811ac6aef8', N'040202', N'0402', N'气血亏虚', 1, N'', N'颜面有暗红或淡红色的斑片，结节浸润，脓水稀薄，老疤痕凹陷，新溃疡色暗，伴神疲乏力，低热盗汗，纳差。舌质淡，苔薄白，脉细缓。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e68d2c00-26e9-469c-ade2-515ba65df668', N'7ff56278-7880-4610-ba61-d6245b863efa', N'040301', N'0403', N'湿热瘀阻', 1, N'', N'结节暗红，压痛明显，多有低热，小腿酸痛，纳差便干。舌红，苔薄白，脉细或数。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
