INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b1358d96-8b21-4952-a408-e9e73abb785d', N'a32700f2-b85c-49d0-b986-9d356fc642c3', N'042001', N'0420', N'风热犯表', 1, N'', N'风团鲜红，灼热剧痒。伴有发烧、恶寒、咽喉肿痛，遇热则皮疹加重。舌苔薄白或薄黄，脉浮数。', CAST(0x00009A79011812D4 AS DateTime), N'', 3)
