INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0aadb2f3-7993-4aac-ae91-9fc0f2c2caa6', N'ca50967f-9424-4e08-bda6-47338520d3b0', N'042101', N'0421', N'风热犯表', 1, N'', N'风团样菱形红斑，中心有小丘疹或水疱。舌尖红，苔薄白，脉浮数。', CAST(0x00009A79011812D4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ae4495d6-6636-463c-b9a9-7b59645b9c0d', N'ca50967f-9424-4e08-bda6-47338520d3b0', N'042102', N'0421', N'胃肠湿热', 1, N'', N'风团红斑，糜烂结痂。伴脘腹痞胀，大便秘结。舌质稍红，苔白腻，脉弦滑。', CAST(0x00009A79011812D4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'270f554f-dbf1-4811-b4f0-996b9ce2d055', N'57ba211e-598e-4b2d-99f9-cf2c63271bdc', N'042201', N'0422', N'湿毒蕴肤', 1, N'', N'皮损处呈红斑、水疱，甚则糜烂渗液，表皮剥脱。伴剧痒，烦躁，口干，大便燥结，小便黄赤，或有发热。舌质红，苔薄白或黄，脉滑或数。', CAST(0x00009A79011812D4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a96d89c1-88a8-4a33-afab-a391f266ac6a', N'57ba211e-598e-4b2d-99f9-cf2c63271bdc', N'042202', N'0422', N'热毒入营', 1, N'', N'皮疹鲜红或紫红，甚则紫斑、血疱，高热神志不清，口唇焦燥，口渴不欲饮，大便干，小便短赤。舌质绛，苔少或镜面舌，脉洪数。', CAST(0x00009A79011812D4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8ba51a18-4dc3-47cc-b114-bc770e2d77a1', N'57ba211e-598e-4b2d-99f9-cf2c63271bdc', N'042203', N'0422', N'气阴两虚', 1, N'', N'皮疹消退，伴低热，口渴，乏力，气短，大便干，尿黄。舌红，少苔，脉细数。', CAST(0x00009A79011812D4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8f636609-aae2-4d4c-8a40-9004a894bbb0', N'6688fcdb-4463-4c15-b8f4-069d2cfdea35', N'042301', N'0423', N'热毒炽盛', 1, N'', N'发病急骤，水疱迅速扩展或增多，糜烂面鲜红。身热口渴，便干溲赤。舌质红绛，苔少或黄，脉弦滑或数。', CAST(0x00009A79011812D4 AS DateTime), N'', 3)
