INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'26703595-716a-47aa-a6fe-6599405e7d70', N'a18c45c7-6a82-4cb6-97d4-e57c29234351', N'042401', N'0424', N'脾虚湿蕴', 1, N'', N'水疱簇集，呈丘疱疹、风团样瘙痒剧烈。腹胀纳呆，便溏，四肢沉重。舌质淡，苔白，脉弦滑。', CAST(0x00009A79011812D4 AS DateTime), N'', 3)
