INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4e128b01-b470-4ccd-ae70-83afd100d810', N'ca9673be-10d9-4e0a-8b1d-c08f16ed7af8', N'042601', N'0426', N'热毒炽盛', 1, N'', N'相当于系统性红蝴蝶疮急性活动期。面部蝶形红斑鲜艳，皮肤紫斑。伴有高热，烦躁口渴，神昏谵语，抽搐，关节肌肉疼痛，大便干结，小便短赤。舌质红绛，苔黄腻，脉洪数或细数。', CAST(0x00009A79011812D4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd66d6c84-52dc-439c-9ca0-2652449a31bf', N'ca9673be-10d9-4e0a-8b1d-c08f16ed7af8', N'042602', N'0426', N'气阴两伤', 1, N'', N'斑疹暗红。伴有不规则发热或持续低热，手足心热，心烦无力，自汗盗汗，面浮红，关节痛，足跟痛，月经量少或闭经。舌红，苔薄，脉细数。', CAST(0x00009A79011812D4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c1fc8ea2-6912-495d-83ae-7d75fa2e03d9', N'ca9673be-10d9-4e0a-8b1d-c08f16ed7af8', N'042603', N'0426', N'脾肾阳虚', 1, N'', N'面色无华，眼睑、下肢浮肿，胸胁胀满，腰膝酸软，面热肢冷，口干不渴，尿少或尿闭。舌质淡胖，苔少，脉沉细。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'73e48bf1-475a-43f7-b5d9-7f106d1f7cf3', N'ca9673be-10d9-4e0a-8b1d-c08f16ed7af8', N'042604', N'0426', N'脾虚肝旺', 1, N'', N'皮肤紫斑。胸胁胀满，腹胀纳呆，头昏头痛，耳鸣失眠，月经不调或闭经。舌紫暗或有瘀斑，脉细弦。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'bad33678-491d-4337-bb60-c737fd57be10', N'ca9673be-10d9-4e0a-8b1d-c08f16ed7af8', N'042605', N'0426', N'气滞血瘀', 1, N'', N'多见于盘状局限型及亚急性皮肤型红蝴蝶疮。红斑暗滞，角栓形成及皮肤萎缩。伴倦怠乏力。舌暗红，苔白或光面舌，脉沉细。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'821b4695-cc7e-4b8c-bcde-7af73ef795f8', N'ddef2ce8-a9dd-4f36-aadf-37d00cdc9d46', N'042701', N'0427', N'寒湿阻滞', 1, N'', N'多见于局限性皮痹。摸之坚硬，蜡样光泽，手捏不起，渐有萎缩。舌质淡或暗，苔薄白，脉沉缓或迟。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'923a1023-5c2b-45bc-a098-3592d4680670', N'ddef2ce8-a9dd-4f36-aadf-37d00cdc9d46', N'042702', N'0427', N'脾肾阳虚', 1, N'', N'多见于系统性皮痹。初起皮损处水肿，逐渐变硬萎缩。自觉乏力，畏寒肢冷，关节痛甚至活动受限，腹胀纳呆，大便溏泻，月经不调或停经。舌淡胖嫩或边有齿痕，脉沉伏。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'85237a1a-9c8a-4642-bfc0-3f91b5b7c56d', N'ef9fb9d7-50f7-450e-9d78-e28e30b03d96', N'042801', N'0428', N'湿热毒结', 1, N'', N'多见于急性发作期。下肢红斑结节，症见高热，心烦汗出，口舌生疮，灼热疼痛，关节酸痛，胸胁闷胀，口苦咽干，妇女带下黄稠。舌红，苔黄，脉弦滑数。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'30444e90-83ef-40bd-975e-cdd558ad6f02', N'ef9fb9d7-50f7-450e-9d78-e28e30b03d96', N'042802', N'0428', N'肝肾阴虚', 1, N'', N'，口、眼、外阴部溃疡时轻时重，反复发作，低热缠绵，手足心热，头昏目眩，口干咽燥，遗精盗汗，月经不调，腰膝酸软。舌红少津或裂纹舌、光面舌，脉细数。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'35eae6fc-e872-4dce-af2b-fe3ad8cd1614', N'ef9fb9d7-50f7-450e-9d78-e28e30b03d96', N'042803', N'0428', N'脾肾阳虚', 1, N'', N'反复出现口腔、外阴溃疡，遇冷加重。病程长，全身乏力，少气懒言，手足不温，食欲不振，畏寒肢冷，下肢浮肿，大便溏稀，遗精阳萎，月经不调。舌淡，苔白，脉沉细。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fa29efa0-1af1-4a72-8267-46d7f1ca3fc5', N'e111efd8-506c-4d38-8d8f-9c0d1f49409b', N'042901', N'0429', N'湿热蕴结', 1, N'', N'发病急，皮损鲜红，中心水疱明显。发热，咽痛，口干，关节痛，便干，尿黄。舌质红，苔白或微黄，脉弦滑或微数。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'979d8a37-6205-407f-bcee-2ed995aceeb4', N'e111efd8-506c-4d38-8d8f-9c0d1f49409b', N'042902', N'0429', N'寒湿阻络', 1, N'', N'皮疹暗红，遇寒加重。下肢沉重，关节痛，小便清长。舌质淡，苔白，脉沉细或缓。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fff8988d-78da-4fa0-bcdf-82a3014722e5', N'1b49b96f-a69c-4477-b4b9-48ac0576b0e5', N'043001', N'0430', N'湿热瘀阻', 1, N'', N'发病急骤，皮下结节，略高出皮面，灼热红肿。伴头痛，咽痛，关节痛，体温增高，口渴，大便干，小便黄。舌质微红，苔白或腻，脉滑微数。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3fd64c55-1a31-4749-8439-da6a362397a5', N'1b49b96f-a69c-4477-b4b9-48ac0576b0e5', N'043002', N'0430', N'寒湿入络', 1, N'', N'皮损暗红，反复缠绵不愈。伴有关节痛，遇寒加重，肢冷，口不渴，大便不干。舌质淡，苔白或白腻，脉沉缓或迟。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a13be79e-e199-4353-b6df-9a467ede7480', N'6ce15cfc-1f03-4ab5-88b8-486ef03f3867', N'043101', N'0431', N'风热蕴肤', 1, N'', N'发病急骤，皮损呈圆形或椭圆形淡红斑片，中心有细微皱纹，表面少量细糠状鳞屑，伴心烦口渴，大便干，尿微黄。舌质红，苔白或薄黄，脉浮微数。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'cb9767fa-4597-4220-8090-a571aff37da3', N'6ce15cfc-1f03-4ab5-88b8-486ef03f3867', N'043102', N'0431', N'风热血燥', 1, N'', N'斑片鲜红或紫红，鳞屑较多，瘙痒较剧，伴有抓痕血痂。舌红，苔少，脉弦数。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b61774b7-4f2d-49fd-a073-46a4171ae352', N'fbcc7440-3df6-46e8-8543-172c364fdd6e', N'043201', N'0432', N'风热血燥', 1, N'', N'皮损鲜红，皮疹不断出现，红斑增多，刮去鳞屑可见发亮薄膜，点状出血，有同形反应。伴心烦口渴，大便干，尿黄。舌质红，舌苔黄或腻，脉弦滑或数。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a43b2ca2-d9a3-4802-90b4-0cd55e72b98c', N'fbcc7440-3df6-46e8-8543-172c364fdd6e', N'043202', N'0432', N'血虚风燥', 1, N'', N'皮损色淡，部分消退，鳞屑较多。伴口干，便干。舌质淡红，苔薄白，脉细缓。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
