INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'47219dc4-f265-4852-9c34-3bf435db2014', N'9e1b6da9-39f5-4d21-b00f-96bdc330b7bb', N'044101', N'0441', N'肺胃热盛', 1, N'', N'急性发病。皮损色红，并有渗出、糜烂、结痂，痒剧。伴心烦口渴，大便秘结。舌质红，苔黄，脉滑数。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
