INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'cfcfa556-1480-4800-a12b-a12137d2be4e', N'9e1b6da9-39f5-4d21-b00f-96bdc330b7bb', N'044103', N'0441', N'血虚风燥', 1, N'', N'皮肤干燥。有糠秕状鳞屑，瘙痒，头发干燥无光，常伴有脱发。舌质红，苔薄白，脉弦。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'aed31aac-ee3c-4fc6-858b-a4f9281537a4', N'432f866d-a069-4bf2-bb0e-78efecf1c511', N'044201', N'0442', N'湿热毒蕴（急性淋病）', 1, N'', N'尿道口红肿，有尿急、尿频、尿痛、淋沥不止，尿液混浊如脂，尿道口溢脓。严重者尿道粘膜水肿，附近淋巴结红肿疼痛。女性宫颈充血、触痛，并有脓性分泌物，可有前庭大腺红肿热痛等。可有发热等全身症状。舌质红，苔黄腻，脉滑数。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4740059f-73c0-4e68-a63b-510550f1e79c', N'432f866d-a069-4bf2-bb0e-78efecf1c511', N'044202', N'0442', N'正虚毒恋（慢性淋病）', 1, N'', N'小便不畅，短涩，淋沥不禁，腰酸腿软，五心烦热。酒后或疲劳易发，食少纳差，女性带下多。舌质淡或有齿痕，苔白腻，脉沉细弱。', CAST(0x00009A7901181400 AS DateTime), N'', 3)
