INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8881fc8b-754f-496b-8f41-4bde0b3812cc', N'f3e5f2d9-31be-408c-ad93-151f022f679f', N'081901', N'0819', N'热毒蕴结', 1, N'', N'乳晕旁结块红肿疼痛，伴有发热，头痛。苔黄腻，脉滑数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e765b16a-a278-4084-8d5f-99a1a577073f', N'f3e5f2d9-31be-408c-ad93-151f022f679f', N'081902', N'0819', N'余毒未清', 1, N'', N'溃后久不收口，脓水淋漓，形成乳漏，时愈时发，局部可有僵硬肿块。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'480241bf-5317-426b-8ead-56200965edbc', N'06da98e8-b46f-416b-bf1c-d67b2b30d5eb', N'082001', N'0820', N'肝郁火旺', 1, N'', N'乳孔溢液，颜色鲜红或暗红。可伴有烦躁易怒，胸闷胁痛，失眠多梦。舌红，苔薄黄，脉弦。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'efa0e7dc-12ec-4c04-922d-a4f8c8d4b418', N'06da98e8-b46f-416b-bf1c-d67b2b30d5eb', N'082002', N'0820', N'脾虚血亏', 1, N'', N'乳孔溢液，颜色淡红或黄色稀水。伴有面黄倦怠，食少，虚烦不眠。舌淡，苔白，脉细弱。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8f0c93f8-f70e-427a-b625-cd3e22e1316e', N'74cb80ea-1bff-4bcc-aeba-841110d6a14d', N'082101', N'0821', N'肝气郁结', 1, N'', N'一般肿块较小，发展缓慢，不红不热，不痛，推之可移。可有乳房不适，胸闷叹息。苔薄白，脉弦。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c9a69a8f-a5a6-4c40-a945-b1d32a46118b', N'74cb80ea-1bff-4bcc-aeba-841110d6a14d', N'082102', N'0821', N'血瘀痰凝', 1, N'', N'一般肿块较大，坚实木硬，重坠不适。胸胁牵痛，烦闷急躁，或有月经不调，痛经等症。舌暗红，苔薄腻，脉弦细。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0525ca26-66fd-4bdc-a35d-9d7f574b34c9', N'e29108bf-0c6c-4a67-b5f3-37424cb70411', N'082201', N'0822', N'气滞血瘀', 1, N'', N'不发热或发热，腹胀，恶心呕吐。苔白腻，脉弦紧。气滞为主者，腹痛绕脐，尚未固定，腹壁柔软；血瘀为主者，痛点固定在右下腹，拒按，有轻度反跳痛。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'09782427-a603-4440-806a-a71325b9931c', N'e29108bf-0c6c-4a67-b5f3-37424cb70411', N'082202', N'0822', N'瘀滞化热', 1, N'', N'右下腹痛加剧，有明显跳痛及肌紧张，发热口干，便秘溲赤。舌质红，苔黄或黄腻，脉弦滑数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9c5b6740-727f-4bbc-a15d-864e6fb11b78', N'e29108bf-0c6c-4a67-b5f3-37424cb70411', N'082203', N'0822', N'热毒炽盛', 1, N'', N'腹痛剧烈，可遍及全腹，有弥漫性压痛，反跳痛及肌紧张，或有界限不清之包块。高热。舌质红绛而干，苔黄厚干燥或黄厚腻，脉弦滑数，或洪大而数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6fc4d0b5-7af1-4cb9-9111-a26a984ec035', N'98d1c7df-baef-4b8c-aef2-57a457e13b1e', N'082301', N'0823', N'肾气亏虚', 1, N'', N'见于婴幼儿，站立、哭叫时肿块增大，平卧时则肿物缩小。肿物过大时，阴囊光亮如水晶。舌苔薄白，脉细滑。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fd8ff038-5410-4071-8f58-c0f585f0abd1', N'98d1c7df-baef-4b8c-aef2-57a457e13b1e', N'082302', N'0823', N'湿热下注', 1, N'', N'阴囊潮湿而热，或有睾丸肿痛，小便赤热。舌红，苔腻，脉数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6fa33626-fda3-4536-adef-a96a5ff1dc54', N'98d1c7df-baef-4b8c-aef2-57a457e13b1e', N'082303', N'0823', N'肾虚寒湿', 1, N'', N'多见于病程长久，阴囊寒冷，皮肤增厚，坠胀不适。可有面色少华，神疲乏力，腰酸腿软，便溏，小便清长。苔白，脉沉细。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9a251cc5-cf79-4c51-889c-494330b3f789', N'98d1c7df-baef-4b8c-aef2-57a457e13b1e', N'082304', N'0823', N'瘀血阻络', 1, N'', N'有睾丸损伤或睾丸有肿瘤病史。能触到肿块伴疼痛，多不能透光。舌质紫暗，苔薄，脉细涩。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'48833477-dc34-4310-9bcf-ae915a873bf7', N'33c518c4-703f-4052-8ebf-bdb9ae3e6d80', N'08240101', N'082401', N'湿热下注', 1, N'', N'多见于成人。睾丸或附睾肿大疼痛，阴囊皮肤红肿，皱纹消失，焮热疼痛，少腹抽痛，脓肿形成时，按之应指。伴有恶寒发热。舌苔黄腻，脉滑数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a527faa1-9b1d-4441-a65c-39240817e569', N'33c518c4-703f-4052-8ebf-bdb9ae3e6d80', N'08240102', N'082401', N'瘟毒下注', 1, N'', N'多见于儿童。多因患痄腮并发（又称卵子瘟）。睾丸肿大疼痛，有恶寒发热，一般不会化脓。苔黄，脉数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b3cec6b4-d0c7-4c49-82ea-febba1366951', N'33c518c4-703f-4052-8ebf-bdb9ae3e6d81', N'08240201', N'082402', N'气滞痰凝', 1, N'', N'附睾结节，子系粗肿，触痛轻微，牵引少腹不适，一般无全身症状。苔薄腻，脉滑。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'74a09bf6-36ce-4ba0-8261-97b21a1077ea', N'33c518c4-703f-4052-8ebf-bdb9ae3e6d81', N'08240202', N'082402', N'阳虚寒凝', 1, N'', N'附睾结节，子系粗肿，无触痛感，阴囊寒冷。可有腰酸，阳痿，遗精。舌质淡或有齿痕，脉沉或细。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a578424a-3326-40d2-ae83-1e7f5612e940', N'03fb439b-d2a9-44c7-9e3a-8c19fe3b7aab', N'082501', N'0825', N'湿痰凝结', 1, N'', N'除肾子酸胀隐痛，附睾硬结，子系呈条索状肿硬外，一般无明显全身症状。苔薄，脉滑。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7b345247-f606-40d5-975e-c4ab4c40c664', N'03fb439b-d2a9-44c7-9e3a-8c19fe3b7aab', N'082502', N'0825', N'阴虚内热', 1, N'', N'肾子与阴囊皮肤粘连，色转暗红，酿脓时有轻微波动。伴有午后潮热，盗汗，倦怠，颧红。舌质红，苔薄黄，脉细数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'dde3131a-396c-40ea-a18d-65a653ad147b', N'03fb439b-d2a9-44c7-9e3a-8c19fe3b7aab', N'082503', N'0825', N'阳虚痰凝', 1, N'', N'肾子酸胀隐痛，附睾硬结不消，或溃后疮口久不愈合。伴有肢冷畏寒，面色淡白，腰酸肢软，肾子阴冷。舌边有齿痕，苔薄白，脉弱无力。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'07fe6f67-e5ea-4eae-a429-a96f37f19fad', N'32abd504-6f65-4530-89d7-af814f529ded', N'082601', N'0826', N'肺热失宣', 1, N'', N'小便不畅或点滴不通。兼见咽干，口燥，胸闷，呼吸不利，咳嗽咯痰。舌质红，苔薄黄，脉滑数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b333e147-fa73-40dc-ad07-cf7206131b96', N'32abd504-6f65-4530-89d7-af814f529ded', N'082602', N'0826', N'湿热下注', 1, N'', N'尿少黄赤，尿频涩痛，点滴不畅，甚至尿闭，小腹胀满。口渴不欲饮，发热或大便秘结。舌质红，苔黄腻，脉数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd30c0d80-2806-4561-90c0-2884e28fdcf8', N'd431a08e-5ecd-4971-8ebb-c4002de099e9', N'052102', N'0521', N'内闭外脱证', 1, N'', N'突然出现面色苍白，四肢厥冷，或汗出不温，皮肤可见花纹，口唇紫绀。严重者口吐咖啡样液，呼吸浅促或节律不匀，两目无神，脉来细数无力，血压明显下降等危象。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2dc7e9d0-69c1-4b77-99ec-f6ea98f3c5a0', N'8e739ce6-1ad6-4048-88d3-de54e87ee801', N'052201', N'0522', N'血虚风燥', 1, N'', N'疹点呈粟粒大小，表面起白屑，形如癣疥，皮肤殷红而干燥。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'73d52b52-26aa-4438-9077-54fbbbc15589', N'8e739ce6-1ad6-4048-88d3-de54e87ee801', N'052202', N'0522', N'湿热浸淫', 1, N'', N'疹点有水液渗出，或呈脓性分泌物，皮肤有粟状隆起，重者可融合成片，延及全身，有腥气，皮肤色红。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'88c4bacd-6eba-45c8-b328-d978f13cc7ac', N'779483ea-0a4a-421b-86a6-06acb215c235', N'052301', N'0523', N'乳食内积', 1, N'', N'面黄少华，烦躁多啼，夜卧不安，食欲不振，腹部胀满，大便溏泄酸臭或便秘，小便短黄或如米泔，伴有低热。舌红，苔腻，脉滑数，指纹紫滞。', CAST(0x00009A7A00014DFC AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fba7ec29-3ea2-442e-9d0b-aec93b88abc2', N'779483ea-0a4a-421b-86a6-06acb215c235', N'052302', N'0523', N'脾虚夹积', 1, N'', N'面色萎黄，形体较瘦，困倦无力，夜寐不安，不思乳食，腹满喜伏卧，大便稀糊。唇舌淡红，苔白腻，脉细而滑，指纹淡滞。', CAST(0x00009A7A00014DFC AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'27b15713-62cc-4113-b2dd-f0b0f928aaa7', N'b4caa70c-e42f-4de2-9fad-572051696b01', N'052401', N'0524', N'气阴不足', 1, N'', N'见于发病初期。面色苍白，神情烦躁，夜寐不安，发稀枕秃，纳呆盗汗，肌肉松软，囟门迟闭。舌苔薄白，脉细软。', CAST(0x00009A7A00014DFC AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3cfb14ed-ce64-482a-8971-c442af93b584', N'b4caa70c-e42f-4de2-9fad-572051696b01', N'052402', N'0524', N'脾肾亏虚', 1, N'', N'见于发病极期。面白虚浮，多汗肢软，神情呆钝，语言迟发，齿生过缓，立迟行迟，头颅方大，肋骨串珠，甚至鸡胸、龟背，下肢弯曲等。舌质淡白，少苔，脉细无力。', CAST(0x00009A7A00014DFC AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2a56bcac-a9e9-4fa3-ba49-9b84a9fca962', N'02a9f1e8-6008-48d8-8d71-bc82ff28260b', N'052501', N'0525', N'脾胃虚弱', 1, N'', N'面黄少华或淡白，食欲不振，神倦之力，或有腹泻便溏。唇舌色淡，苔薄，脉弱。', CAST(0x00009A7A00014DFC AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3a37fe8b-74d0-4748-915a-15ebeea657c8', N'02a9f1e8-6008-48d8-8d71-bc82ff28260b', N'052502', N'0525', N'心脾两虚', 1, N'', N'面色萎黄或淡白，发焦易脱，倦怠无力，食少纳呆，心悸气短，头晕，口唇粘膜苍白，爪甲色淡。舌质淡胖，苔薄，脉虚细。', CAST(0x00009A7A00014DFC AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd137c50b-2f36-46ae-b33d-779ac130212d', N'02a9f1e8-6008-48d8-8d71-bc82ff28260b', N'052503', N'0525', N'肝肾阴虚', 1, N'', N'两颧嫩红，目眩耳鸣，腰腿酸软，潮热盗汗，口干舌燥，指甲枯脆，肌肤不泽。舌红，少苔，脉细数。', CAST(0x00009A7A00014DFC AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f011f938-41c0-4370-8f04-ff92fa5144ba', N'02a9f1e8-6008-48d8-8d71-bc82ff28260b', N'052504', N'0525', N'脾肾阳虚', 1, N'', N'面色、口唇淡白，畏寒肢冷，食少便溏，消瘦或浮肿，自汗神疲。舌质淡胖，脉沉细。', CAST(0x00009A7A00014DFC AS DateTime), N'', 3)
