INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'195b82c6-c395-49cb-bba3-15b95fc14c12', N'305694af-7bb8-45de-aef9-0cabd48a6525', N'070302', N'0703', N'痰热壅肺', 1, N'', N'身热烦渴，汗出，咳嗽气粗，或痰黄带血，胸闷胸痛，口渴。舌红苔黄，脉洪数或滑数。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
