INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'41022923-bfb4-43da-851e-da2708e1c310', N'305694af-7bb8-45de-aef9-0cabd48a6525', N'070304', N'0703', N'热闭心包', 1, N'', N'壮热，烦躁不安，口渴不欲饮，甚则神昏谵语、痉厥或四肢厥冷。舌绛少津，苔黄，脉弦数或沉数。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'45c8aba4-1282-4cea-b746-55ad4aad41e2', N'305694af-7bb8-45de-aef9-0cabd48a6525', N'070305', N'0703', N'气阴两虚', 1, N'', N'身热渐退，干咳痰少而粘，自汗神倦，纳少口干。舌红少苔，脉细或细数。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'283d0d40-2772-44e1-9410-ce174c07d514', N'305694af-7bb8-45de-aef9-0cabd48a6525', N'070306', N'0703', N'邪陷正脱', 1, N'', N'呼吸短促，鼻翼煽动，面色苍白，大汗淋漓，甚则汗出如油，四肢厥冷，紫绀，烦躁不安，身热骤降。或起病无身热，面色淡白，神志逐渐模糊。舌质淡紫，脉细数无力，或脉微欲绝。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e80782f2-e2a0-4515-956b-9f0a842d40cd', N'fd2be8fe-8064-4c6a-9784-b965e581366c', N'070401', N'0704', N'初期', 1, N'', N'恶寒发热，咳吐白色粘痰，胸痛，咳时加重。舌苔薄黄或薄白，脉浮滑数。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
