INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0f3e2e62-062e-44d7-8c05-f565d36a6de3', N'fd2be8fe-8064-4c6a-9784-b965e581366c', N'070403', N'0704', N'溃脓期', 1, N'', N'咳吐脓血，状如米粥，量多腥臭，胸满，心烦懊依。舌质红绛，苔黄腻，脉滑数。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
