INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ed042197-128b-4828-87da-58fe14688f7d', N'e0266cae-83ae-4168-9572-fbd03f9eaa62', N'070504', N'0705', N'阴阳两虚', 1, N'', N'咳逆喘息，痰呈泡沫状或夹血，形寒自汗，声嘶音哑，形体消瘦。或伴有浮肿、腹泻等症。舌质淡而少津，苔光剥，脉微数或虚大无力。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
