INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0824ab9e-0eed-4d1d-870c-801e91347ace', N'54c9d129-2b5c-49f3-ac22-60febd35911c', N'050601', N'0506', N'伤食泻', 1, N'', N'大便酸臭，或如败卵，腹部胀满，口臭纳呆，泻前腹痛哭闹，多伴恶心呕吐。舌苔厚腻，脉滑有力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b9f07cae-0d3e-434d-b108-caa4a99ace2c', N'54c9d129-2b5c-49f3-ac22-60febd35911c', N'050602', N'0506', N'风寒泻', 1, N'', N'大便色淡，带有泡沫，无明显臭气，腹痛肠鸣。或伴鼻塞，流涕，身热。舌苔白腻，脉滑有力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
