INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5030dd54-3dac-48f8-9f98-58d568cbccff', N'54c9d129-2b5c-49f3-ac22-60febd35911c', N'050604', N'0506', N'寒湿泻', 1, N'', N'大便每日数次或十数次，色较淡，可伴有少量粘液，无臭气，精神不振，不渴或渴不欲饮，腹满。舌苔白腻，脉濡。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c014209e-02fc-4f14-a151-acaf6df67e94', N'54c9d129-2b5c-49f3-ac22-60febd35911c', N'050605', N'0506', N'脾虚泻', 1, N'', N'久泻不止，或反复发作，大便稀薄，或呈水样，带有奶瓣或不消化食物残渣，神疲纳呆，面色少华。舌质偏淡，苔薄腻，脉弱无力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0469e7d5-cb0c-468d-99ef-833a38fa4573', N'54c9d129-2b5c-49f3-ac22-60febd35911c', N'050606', N'0506', N'脾肾阳虚泻', 1, N'', N'大便稀溏，完谷不化，形体消瘦，或面目虚浮，四肢欠温。舌淡苔白，脉细无力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
