INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e3d33632-76ea-43ef-9bed-6fc30389e582', N'f30decc7-48ed-499f-9b75-9c249186b214', N'050702', N'0507', N'脾胃气虚', 1, N'', N'厌食或拒食，面色萎黄，精神稍差，肌肉松软。或形体消瘦，大便多不成形，或夹不消化食物。舌质淡，苔薄白，脉无力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9d174831-1977-43a3-b3e3-20b17afb6381', N'f30decc7-48ed-499f-9b75-9c249186b214', N'050703', N'0507', N'脾胃阴虚', 1, N'', N'厌食或拒食，面色萎黄，形瘦，口干食少饮多，甚则每食必饮，烦热不安，便干溲赤。舌质红，苔净或花剥，脉细无力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6074a9e9-aa7e-4fd5-a3ab-05d2de4dec54', N'f30decc7-48ed-499f-9b75-9c249186b214', N'050704', N'0507', N'肝旺脾虚', 1, N'', N'厌食或拒食，性躁易怒，好动多啼，咬齿磨牙，便溏溲少。舌光苔净，脉细弦。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0176eb06-8c20-48a4-9560-41807b7f80a3', N'446ee709-7376-45c1-83ef-3815c5943a8e', N'050801', N'0508', N'疳气', 1, N'', N'形体略见消瘦，面色稍萎黄，食欲不振，或食多便多，大便干稀不调，精神不振，好发脾气。舌苔腻，脉细滑。多见于本病之初期。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3dddc096-672e-413b-9488-7804e207ba71', N'446ee709-7376-45c1-83ef-3815c5943a8e', N'050802', N'0508', N'疳积', 1, N'', N'形体消瘦明显，脘腹胀大，甚则青筋暴露，面色萎黄，毛发稀疏易落，烦躁。或见揉眉挖鼻，吮指磨牙，食欲减退。或善食易饥、大便下虫。或嗜食生米、泥土等异物。舌质偏淡，苔淡黄而腻，脉濡细而滑。多见于本病之中期。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'913d0696-4c9b-4067-aa45-eaeaefd6c895', N'446ee709-7376-45c1-83ef-3815c5943a8e', N'050803', N'0508', N'干疳', 1, N'', N'极度消瘦，皮包骨头，呈老人貌，皮肤干枯有皱纹，精神萎靡，啼哭无力，无泪。或可见肢体浮肿。或见紫癜、鼻衄、齿衄等。舌淡或光红少津，脉弱。多见于本病之晚期。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4f5c2aeb-6698-4e70-a1c1-1595d30e20fd', N'6b70500c-8b09-4a9d-8cca-2f8a20439501', N'050901', N'0509', N'风水相搏', 1, N'', N'眼睑浮肿，继则全身浮肿，肢节酸痛，尿少。或伴发热咳嗽，舌苔薄白，脉浮。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c076c934-931f-42f1-a84b-cac6438b88eb', N'6b70500c-8b09-4a9d-8cca-2f8a20439501', N'050902', N'0509', N'湿热内蕴', 1, N'', N'肢体浮肿，小便短赤，或肉眼血尿，发热神烦。或有皮肤疮毒。舌苔黄腻，脉略数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ab8faf0b-ab6a-45ed-9201-1fc3077a0874', N'6b70500c-8b09-4a9d-8cca-2f8a20439501', N'050903', N'0509', N'脾虚湿困', 1, N'', N'肢体浮肿，面色萎黄，疲乏无力，胸闷腹胀，纳少便溏，小便短少，舌质偏淡，苔白滑，脉濡。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'15a38075-8c23-4e40-a1c5-2d04c02156cb', N'6b70500c-8b09-4a9d-8cca-2f8a20439501', N'050904', N'0509', N'脾肾阳虚', 1, N'', N'全身高度浮肿，或有胸水、腹水，面色淡白或灰暗，神疲畏寒，四肢欠温，脘腹胀满。甚至咳逆上气，不能平卧。舌淡胖，苔白，脉沉细。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'901ca108-8eb9-4449-b697-92da002ef581', N'6b70500c-8b09-4a9d-8cca-2f8a20439501', N'050905', N'0509', N'肺脾气虚', 1, N'', N'面肢浮肿、淡白无华，神疲乏力，动则自汗。易于感冒，感冒后常咳嗽不沐，伴有痰声，纳谷不馨，大便偏溏，溲清。舌淡，苔薄白，脉细弱。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1574c3b5-93e5-41a1-948e-8080c2f80e35', N'4726022d-0426-48b0-9796-860cf7917405', N'05100101', N'051001', N'疹前期（初热期）', 1, N'', N'从开始发热到出疹3天左右。热势渐升，咳嗽，流涕，目赤畏光，泪水汪汪.汪。舌苔薄白或薄黄，脉浮数。疹点均匀，色泽红，疹子透齐后即顺序消退，同时热退咳减，无合并症发生。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'44101f68-976c-49c5-bd2d-9bbee5764579', N'4726022d-0426-48b0-9796-860cf7917405', N'05100102', N'051001', N'出疹期（见形期）', 1, N'', N'皮疹从见点到透齐3天左右。发热不退，咳嗽加剧，疹点先见于耳后、发际，渐及头面，胸背、腹部、四肢，最后手掌足底见疹，即为出齐。疹色呈暗红色的斑丘疹。舌质红，苔黄，脉洪数。疹点均匀，色泽红，疹子透齐后即顺序消退，同时热退咳减，无合并症发生。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2fc32e37-b93c-424e-8368-bcb8f0d6f65f', N'4726022d-0426-48b0-9796-860cf7917405', N'05100103', N'051001', N'疹回期（收没期）', 1, N'', N'从疹点透齐至收没3天左右。发热渐退，咳嗽减轻，疹点依次渐回。疹退处皮肤呈糠状脱屑，留有色素沉着。舌质红，少津，苔少，脉细弱或细数。疹点均匀，色泽红，疹子透齐后即顺序消退，同时热退咳减，无合并症发生。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9b8cc01a-3713-4b1f-b01b-0871ef1f2c3e', N'4726022d-0426-48b0-9796-860cf7917406', N'05100201', N'051002', N'热毒闭肺', 1, N'', N'高热烦躁，咳嗽气促，鼻翼煽动，喉间痰鸣，疹点紫暗或隐没，甚则面色青灰，口唇紫绀。舌红，苔薄黄或黄腻而干，脉数有力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c178ff42-35ba-4928-b630-2c545f5f7f48', N'4726022d-0426-48b0-9796-860cf7917406', N'05100202', N'051002', N'热毒攻喉', 1, N'', N'身热不退，咽喉肿痛，声音嘶哑，或咳声重浊，状如犬吠，喉间痰鸣，甚则呼吸困难，面色发紫，烦躁不安。舌质红，苔黄腻，脉数有力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8f1eb3e3-cb79-4fb5-83fd-2e4974bdfb9d', N'4726022d-0426-48b0-9796-860cf7917406', N'05100203', N'051002', N'毒陷心肝', 1, N'', N'高热，烦躁，谵语，皮肤疹点密集成片，色紫红，或见鼻衄，甚则神昏抽搐。舌绛起刺，苔黄糙，脉数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5ec1b685-d43c-4eea-af6d-842912d8e29c', N'8811e45f-75c8-43d8-b6ea-6872063672dc', N'051101', N'0511', N'邪郁肺卫', 1, N'', N'发热恶风，喷嚏流涕，咳嗽，疹色浅红，先起于头面，继及于身躯各部分，分布均匀，稀疏细小，2～3日后消退，有痒感。舌苔薄黄，脉浮数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ce8b06d8-888e-40ae-a6c8-487a0cfc82fe', N'8811e45f-75c8-43d8-b6ea-6872063672dc', N'051102', N'0511', N'邪热炽盛', 1, N'', N'高热口渴，心烦不宁，疹色鲜红或紫暗，小便短赤，大便秘结。舌质红，苔黄厚或黄糙，脉数有力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'888a8601-3311-4254-80b9-f607e07530e9', N'e4bfe5da-a85d-4fa2-8b87-f7e7ddf9c9d2', N'051201', N'0512', N'邪侵肺卫', 1, N'', N'畏寒发热，咽红肿痛，皮疹隐隐。舌尖红，苔薄白，脉浮数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'60efed0c-3f8c-41b6-8c73-1bc1726a08be', N'e4bfe5da-a85d-4fa2-8b87-f7e7ddf9c9d2', N'051202', N'0512', N'邪人气营', 1, N'', N'高热，烦躁不安，口渴欲饮，咽部红肿疼痛，甚则溃烂，皮疹成片，猩红若丹。若热毒内陷，出现高热昏迷，烦躁谵语，或有抽风。皮疹呈紫红色，或伴有瘀点。舌绛起刺，苔剥，脉数有力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'20d371b0-2dd9-4e36-9885-7c07a379bf0f', N'e4bfe5da-a85d-4fa2-8b87-f7e7ddf9c9d2', N'051203', N'0512', N'疹后阴伤', 1, N'', N'身热渐退，皮疹渐消，继则脱屑退皮，咽部赤烂疼痛渐减，午后或有低热。舌红有刺，脉细数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b2de8397-0e0a-43ea-bf4d-2491d507be9a', N'cadd2374-0df7-4a70-856d-2ae065ebc1f3', N'051301', N'0513', N'风热犯表', 1, N'', N'发热轻微，鼻塞流涕，偶有喷嚏及咳嗽，疹色淡红而润，疱浆清亮，点粒稀疏，躯干为多，二便如常。舌苔薄白，脉浮数或略数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'54331d8f-1ec2-4691-9adf-5df019f6656b', N'cadd2374-0df7-4a70-856d-2ae065ebc1f3', N'051302', N'0513', N'热毒炽盛', 1, N'', N'壮热烦渴，面赤唇红，便秘溲赤，痘大而密，疹色红赤或紫暗，疱浆较混，根脚较硬，口、眼等处亦见疱疹或溃疡。舌苔黄糙而于，脉数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'954a4966-9555-473a-986f-5ef598f6921f', N'4726022d-0426-48b0-9796-860cf7917402', N'051401', N'0514', N'温毒袭表', 1, N'', N'发热轻，一侧或两侧耳下腮部肿大，压之疼痛有弹性感。舌尖红，苔薄白，脉浮数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'28ef7465-dca0-4e4e-b3cd-cb1b4facf071', N'4726022d-0426-48b0-9796-860cf7917402', N'051402', N'0514', N'热毒蕴结', 1, N'', N'壮热，头痛，烦躁，腮部漫肿，疼痛拒按。舌红苔黄，脉数有力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'99bdc558-dd23-4e06-b734-8051868157d5', N'4726022d-0426-48b0-9796-860cf7917402', N'051403', N'0514', N'毒陷心肝', 1, N'', N'腮部肿胀，高热不退，嗜睡，颈强，呕吐，甚则昏迷，抽风。舌质红绛，苔黄糙，脉洪数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b400a936-9d28-4f70-875a-48d857b108b6', N'4726022d-0426-48b0-9796-860cf7917402', N'051404', N'0514', N'邪窜肝经', 1, N'', N'腮部肿胀，发热，男性睾丸肿痛，女性少腹痛。舌质偏红，苔黄，脉弦数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7460cfcb-efb8-4451-842a-76e3a0120dd0', N'8a5810e2-7932-4fe2-8237-b7c43dd76b7f', N'051501', N'0515', N'初咳期', 1, N'', N'微热，喷嚏，咳嗽逐渐加重，昼轻夜重。偏于风寒者，伴恶寒，痰稀色白，舌苔薄白，脉浮紧；偏于风热者，伴咽红，痰稠不易咯出，舌苔薄黄，脉浮数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1c9c7760-7754-4176-84b7-f6579615a77c', N'8a5810e2-7932-4fe2-8237-b7c43dd76b7f', N'051502', N'0515', N'痉咳期', 1, N'', N'咳嗽阵作，侯轻夜重，咳时面红耳赤，涕泪交流，咳后回吼，甚至吐出乳食痰液后，痉咳方可暂停。剧咳时可见痰中带血丝，甚则鼻衄或结膜下出血，可见舌系带溃疡。舌苔黄，脉数有力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'17b763ba-337b-44d0-94ae-9eaa540e0030', N'8a5810e2-7932-4fe2-8237-b7c43dd76b7f', N'051503', N'0515', N'恢复期', 1, N'', N'', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'53830c3e-041f-4ebf-9a01-a8b4e599bfed', N'8a5810e2-7932-4fe2-8237-b7c43dd76b7f', N'051504', N'0515', N'脾气亏虚', 1, N'', N'形体虚弱，咳声低微，痰多稀白，纳呆便溏，神疲乏力。舌质偏淡，苔薄白，脉沉有力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fceaa46e-2afb-4d63-b257-61561af37af8', N'8a5810e2-7932-4fe2-8237-b7c43dd76b7f', N'051505', N'0515', N'肺阴亏虚', 1, N'', N'形体虚弱，干咳少痰，两颧发红，手足心热，夜寐盗汗。舌质偏红，少苔，脉细数无力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f022b913-4178-4f3b-b00c-4ea593789587', N'9cb534e1-babe-480a-8c95-737687c91de2', N'051601', N'0516', N'卫气同病', 1, N'', N'突然发热，常伴有呕吐。嗜睡，较大儿童可自诉头痛，怕风，神志多清醒。高热时可有惊厥或烦躁不宁。夹湿者，舌苔白腻；偏热者，舌红，苔黄，脉浮数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c47e6e17-8f2d-4f9c-8cca-e1e75da973b3', N'9cb534e1-babe-480a-8c95-737687c91de2', N'051602', N'0516', N'气营两燔', 1, N'', N'持续高热，神志不清或时清时昧，颈项强直，四肢抽搐，甚则喉间痰声漉漉，大便秘结。舌质红绛，苔多黄糙或灰暗，脉数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f57a76b7-7fc7-4762-a269-e82d4db1822d', N'9cb534e1-babe-480a-8c95-737687c91de2', N'051603', N'0516', N'热入营血', 1, N'', N'发热不退，抽搐频繁，牙关紧闭，两目上视，面色灰暗，可见呕血，甚或突然呼吸停止，肢厥脉绝，卒然闭脱。舌质紫暗而干，或舌卷僵硬，脉细数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'408be97e-bfaa-449e-bedf-f45f0a732c9b', N'9cb534e1-babe-480a-8c95-737687c91de2', N'051604', N'0516', N'余热未清', 1, N'', N'低热或不规则发热，口干喜饮，两颧潮红，舌光红少苔，或舌红起刺者，为病后津液耗伤，属阴虚发热；如汗出不温，面色淡白，舌质淡嫩，苔薄白者，为营虚卫弱，阳气不足。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'977288ec-b701-4ad4-a4a7-bc225eae0cda', N'9cb534e1-babe-480a-8c95-737687c91de2', N'051605', N'0516', N'痰蒙清窍', 1, N'', N'面色少华，意识不清或痴呆，失语，喉有痰声，吞咽困难，或狂躁不宁。舌质红，苔黄者，为痰火内扰；深度昏迷，舌质不红，苔厚腻者，为痰浊内蒙。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a88a8ec5-4cb1-42da-934e-3427649494dd', N'9cb534e1-babe-480a-8c95-737687c91de2', N'051606', N'0516', N'肝风内动', 1, N'', N'其肢体呈强直性瘫痪或角弓反张者，为风窜络脉；若震颤抖动，不自主动作者，为阴虚风动。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1061ad87-7ab6-4589-bd38-2f81f44672bb', N'ca31a2aa-fc6e-47de-b6c3-9fff05ab59cc', N'051701', N'0517', N'暑伤肺胃', 1, N'', N'发热，口渴，多尿，无汗或少汗，烦躁不安，口唇干红。舌质红，苔薄白或薄黄，脉数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'604817e7-7751-4032-af56-c22b721cfca7', N'ca31a2aa-fc6e-47de-b6c3-9fff05ab59cc', N'051702', N'0517', N'上盛下虚', 1, N'', N'发热，口渴多饮，多尿次频，汗闭，精神萎靡，虚烦不安，面色淡白，下肢欠温，胃纳减退，或大便稀薄。舌质淡，苔簿，脉细数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0f3a84c3-cef6-48ab-8a5b-000158ef9650', N'c00cdad0-0123-4831-9049-e55f82b4a2ea', N'051801', N'0518', N'湿困脾胃', 1, N'', N'胸闷泛恶，倦怠乏力，面色萎黄，食欲不振，身热不扬，大便不调，小便色黄。舌质淡红，苔白腻或微黄腻，脉濡数。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c1c9d8a5-ab35-4ce3-87e0-352c5340922f', N'c00cdad0-0123-4831-9049-e55f82b4a2ea', N'051802', N'0518', N'脾胃气虚', 1, N'', N'精神萎靡，倦怠乏力，口淡无味，饮食少思，大便溏薄。舌质偏淡，苔薄白，脉濡缓。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'41a7ade8-7c7b-4e6c-a736-721c31440a92', N'2bcfb60a-48b6-40e7-9bc8-ea09b38c39a8', N'051901', N'0519', N'肾气不足', 1, N'', N'睡中遗尿，尿量多，尿色清，熟睡，不易叫醒，面色淡白，精神不振，形寒肢冷。舌质淡，苔白，脉沉迟无力。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ce0fff66-02bc-4684-9814-902bfb73322a', N'2bcfb60a-48b6-40e7-9bc8-ea09b38c39a8', N'051902', N'0519', N'脾肺气虚', 1, N'', N'睡中遗尿，尿频而量多，面色无华，神疲乏力，食欲不振，大便溏薄。舌偏淡，脉缓细。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4e89e3c3-51ca-43af-ba81-426127897570', N'2bcfb60a-48b6-40e7-9bc8-ea09b38c39a8', N'051903', N'0519', N'肝经湿热', 1, N'', N'睡中遗尿，尿频量少，性情急躁，手足心热，唇红而干。舌质红，苔黄，脉弦滑。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b2b5d168-996e-455a-83f0-8397deba4be8', N'186120d2-8b08-4f10-9309-2af18ef0650a', N'052001', N'0520', N'湿热内蕴', 1, N'', N'面目皮肤发黄，颜色鲜明，状如橘色，烦躁啼哭，小便黄赤，大便秘结或灰白。舌红，苔黄厚腻，指纹滞。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ef6bbaa7-2619-407b-9a14-2afe6363649c', N'186120d2-8b08-4f10-9309-2af18ef0650a', N'052002', N'0520', N'脾虚湿困', 1, N'', N'面目皮肤发黄，颜色晦暗，精神倦怠，不欲吮乳，时时啼哭，腹胀便溏，或大便灰白，小便黄少。唇舌偏淡，苔白滑，指纹淡。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'cfbab0b5-58f4-4e10-9e50-2543c0761dbc', N'186120d2-8b08-4f10-9309-2af18ef0650a', N'052003', N'0520', N'气血瘀滞', 1, N'', N'面目皮肤发黄，颜色晦滞，日益加重，腹部胀满，青筋暴露，肝脾肿大质硬，小便短黄，大便秘结或灰白，唇色暗红，或衄血。舌见瘀点，指纹紫。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e4713bc9-1837-46f8-a556-5be3287e1eed', N'd431a08e-5ecd-4971-8ebb-c4002de099e9', N'052101', N'0521', N'疫毒内闭证', 1, N'', N'突然高热，恶心呕吐，烦躁不宁，甚则反复惊厥，神志欠清，或痢下脓血，舌质红，苔黄或灰糙，脉数有力。。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a898f98f-b792-47d1-a0a5-eee8128e3634', N'a39ff4b3-cd9d-4df9-9711-eb15d1a39fe7', N'070602', N'0706', N'阴虚火旺', 1, N'', N'反复咯血，血色鲜红，干咳咽燥。舌红苔黄少津，脉细数。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3d6da817-d5df-4d00-b510-85af7b050a3f', N'a39ff4b3-cd9d-4df9-9711-eb15d1a39fe7', N'070603', N'0706', N'痰热壅肺', 1, N'', N'咯（咳）血量多，血色鲜红或夹有黄痰，或脓痰腥臭，心烦口渴。舌红苔黄腻，脉滑数。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'62eba1bc-a9f0-47d3-a9f3-70f5a4930592', N'a39ff4b3-cd9d-4df9-9711-eb15d1a39fe7', N'070604', N'0706', N'气虚血瘀', 1, N'', N'反复咯（咳）血，血色淡红或夹紫黯血块，气短胸闷，易汗。舌淡或有紫色瘀斑，苔薄白，脉细涩。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'74319a35-33d0-4072-ae87-aa4d95af1bab', N'79f76a66-ee21-4ae9-b0e2-f612c16bc1ff', N'070701', N'0707', N'邪郁少阳', 1, N'', N'寒热往来，或恶寒发热，胸胁疼痛，咳嗽痰少。舌苔薄白或黄，脉弦数。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2bd31a9c-105d-40f0-8c2c-72e52b9a3c6a', N'79f76a66-ee21-4ae9-b0e2-f612c16bc1ff', N'070702', N'0707', N'饮停胸胁', 1, N'', N'咳唾时胸胁引痛，转侧不利，偏卧于病侧则痛缓，肋间胀满，呼吸息促。舌苔薄白，脉象沉弦。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'653793e5-1a04-49a0-828b-9a0128a88fac', N'79f76a66-ee21-4ae9-b0e2-f612c16bc1ff', N'070703', N'0707', N'肺络不畅', 1, N'', N'胸胁疼痛，呼吸不畅，或有闷咳，迁延不已。百苔薄，脉弦细。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd0ab2b3f-2f33-464e-9a09-61192d2225e1', N'497f4299-c6fc-48ea-9420-ee4f5d47b841', N'070801', N'0708', N'风寒束肺', 1, N'', N'喘急胸闷，咳嗽痰多清稀，伴有恶寒发热，头痛等症。舌苔薄白，脉浮紧。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'44c8df81-6fdc-4668-9e68-3591bc6ba5b2', N'497f4299-c6fc-48ea-9420-ee4f5d47b841', N'070802', N'0708', N'风热犯肺', 1, N'', N'喘促气粗，咳嗽痰黄而稠粘，心胸烦闷，口干而渴，可有发热恶风。舌边红，苔薄黄，脉浮数。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3931be0f-6ea4-41b3-a6fc-33f9a8482400', N'497f4299-c6fc-48ea-9420-ee4f5d47b841', N'070803', N'0708', N'痰湿蕴肺', 1, N'', N'喘咳胸闷，痰多易咯，痰粘或咯吐不爽，胸中窒闷，口腻，脘痞腹胀。舌质淡，舌苔白腻，脉弦滑。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7f8f96f8-6dba-46f5-9f41-07d7c4a8dd38', N'497f4299-c6fc-48ea-9420-ee4f5d47b841', N'070804', N'0708', N'水气凌心', 1, N'', N'气喘息涌，痰多呈泡沫状，胸满不能平卧，肢体浮肿，心悸怔忡，尿少肢冷，舌苔白滑，脉弦细数。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'cc9e758a-01ca-4d18-900d-ee5fef86b6a6', N'497f4299-c6fc-48ea-9420-ee4f5d47b841', N'070805', N'0708', N'肺脾两虚', 1, N'', N'喘息短促无力，语声低微，自汗心悸，面色 白，神疲乏力，食少便塘，舌淡苔少，脉弱。或口干咽燥，舌红，脉细。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2c0a3216-6b09-4b7b-8d4d-28fbb38b6fbf', N'497f4299-c6fc-48ea-9420-ee4f5d47b841', N'070806', N'0708', N'肺肾两虚', 1, N'', N'喘促日久，心悸怔忡，动则喘咳，气不接续，胸闷如窒，不能平卧，痰多而粘，或心烦不寐，唇甲紫绀。舌质紫或舌红苔少，脉微疾或结、代。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2ea72801-d259-439e-92b6-eb48b68002d6', N'8219b66f-4567-4647-a700-704b45439f29', N'07090101', N'070901', N'冷哮', 1, N'', N'喉中哮鸣有声，胸膈满闷，咳痰稀白，面色晦滞。或有恶寒、发热、身痛。舌质淡，苔白滑，脉浮紧。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b4e54049-8c7d-4bf6-9da7-b939ae6bdb29', N'8219b66f-4567-4647-a700-704b45439f29', N'07090102', N'070901', N'热哮', 1, N'', N'喉中哮鸣如吼，气粗息涌，胸膈烦闷，呛咳阵作，痰黄粘稠，面红，伴有发热、心烦口渴。舌质红，苔黄腻，脉滑数。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3aa8b973-8875-4d9a-823f-7b35504851e3', N'8219b66f-4567-4647-a700-704b45439f29', N'07090103', N'070901', N'虚哮', 1, N'', N'反复发作，甚者持续喘哮，咯痰无力，声低气短，动则尤甚，唇爪甲紫绀。舌质紫暗，脉弱。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'75488fb1-fbbb-4e1f-bc5e-6857524e442f', N'8219b66f-4567-4647-a700-704b45439f30', N'07090201', N'070902', N'肺气亏虚', 1, N'', N'平素自汗，怕风，常易感冒，每因气候变化而诱发。发病前喷嚏频作，鼻塞流清涕。舌苔薄白，脉濡。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0ab07a65-358a-4685-8caf-b8d6beb5035f', N'8219b66f-4567-4647-a700-704b45439f30', N'07090202', N'070902', N'脾气亏虚', 1, N'', N'平素痰多，倦怠无力，食少便溏，每因饮食失当而引发。舌苔薄白，脉细缓。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0f9eec6c-f86b-485e-8ede-e436527063ba', N'8219b66f-4567-4647-a700-704b45439f30', N'07090203', N'070902', N'肾气亏虚', 1, N'', N'平素气息短促，动则为甚。腰酸腿软，脑转耳鸣，不耐劳累，下肢欠温，小便清长。舌淡，脉沉细。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'133ccfcd-9e63-4e3b-873d-d181cf204e4d', N'dc721271-afd0-4be7-b9c1-08ff2a847d95', N'071001', N'0710', N'肝胃气滞', 1, N'', N'胃脘痞胀疼痛或攻窜胁背，嗳气频作。苔薄白，脉弦。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
