INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'03e23263-f04a-4a59-ab2a-cd03089ade4b', N'6ea8328b-0849-4957-81bf-16b9ea0b6b54', N'071605', N'0716', N'脾气亏虚', 1, N'', N'大便溏薄，夹有不消化食物，稍进油腻则便次增多，伴有神疲乏力。舌质淡，苔薄白，脉细。', CAST(0x00009A7A01032AE0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2ccb7e57-01dc-4d30-bdc1-478651bba5e3', N'6ea8328b-0849-4957-81bf-16b9ea0b6b54', N'071606', N'0716', N'肾阳亏虚', 1, N'', N'晨起泄泻，大便夹有不消化食物，脐腹冷痛，喜暖，形寒肢冷，舌淡胖，苔白，脉沉细。', CAST(0x00009A7A01032AE0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd1e22246-8974-4f7d-8fe4-9602b32f80fd', N'83886669-4da0-48aa-9dde-c1169e25be3c', N'071701', N'0717', N'肝胆湿热', 1, N'', N'身目俱黄，黄色鲜明。发热口渴，心中懊憹，口干而苫，恶心欲吐，腹满胁痛，大便秘结或呈灰白色，小便短黄。舌红、苔黄腻，脉弦数。', CAST(0x00009A7A01032AE0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'78e370e2-d5fb-49fd-afb0-1a6f70fc6dc7', N'83886669-4da0-48aa-9dde-c1169e25be3c', N'071702', N'0717', N'湿困脾胃', 1, N'', N'身目俱黄，黄色晦滞，头重身困，胸脘痞满，恶心纳少，腹胀，大便溏垢。苔腻微黄，脉弦滑或濡缓。', CAST(0x00009A7A01032AE0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'cad0b7eb-0a47-4b99-8589-22e33163f105', N'32abd504-6f65-4530-89d7-af814f529ded', N'082603', N'0826', N'中气下陷', 1, N'', N'小腹坠胀，小便欲解不爽，尿失禁或夜间遗尿。精神倦怠，少气懒言。舌质淡，苔薄白，脉濡细。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ad51d34d-25e2-43b9-8116-a6060cbee9cf', N'32abd504-6f65-4530-89d7-af814f529ded', N'082604', N'0826', N'肾阴亏虚', 1, N'', N'小便频数不爽，淋漓不尽。伴有头晕目眩，腰酸膝软，失眠多梦，咽干。舌红，苔黄，脉细数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'53a18b8d-6923-46d2-874c-46322ae21a6d', N'32abd504-6f65-4530-89d7-af814f529ded', N'082605', N'0826', N'肾阳虚损', 1, N'', N'排尿无力，失禁或遗尿，点滴不尽。面色huang(恍)白，神倦畏寒，腰膝酸软无力，手足不温。舌质淡。苔白，脉沉细。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'cfc96909-0621-4750-acd9-3c8905a20ae1', N'32abd504-6f65-4530-89d7-af814f529ded', N'082606', N'0826', N'气滞血瘀', 1, N'', N'小便努责方出或点滴全无，会阴、小腹胀痛，偶有血尿或血精。舌质紫暗或有瘀斑，苔白或黄，脉沉弦或细涩。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'068271e0-263e-4e06-8ce1-35d88e87b669', N'7934a3b3-43ff-4be4-9b63-b80f5789c3ae', N'082701', N'0827', N'气滞血瘀', 1, N'', N'少腹、会阴、睾丸坠胀不适，或有血尿，血精。舌质紫或瘀点，苔白或黄，脉沉涩。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'18f45ed7-1662-413b-9de8-6aec42cff99d', N'7934a3b3-43ff-4be4-9b63-b80f5789c3ae', N'082702', N'0827', N'湿热蕴结', 1, N'', N'尿频、尿急、尿痛，有灼热感，排尿或大便时尿道有白浊溢出。会阴、腰骶、睾丸有坠胀疼痛。苔黄腻，脉滑数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'39bdd077-77f5-4ccd-9f18-141d07839ec3', N'7934a3b3-43ff-4be4-9b63-b80f5789c3ae', N'082703', N'0827', N'阴虚火旺', 1, N'', N'腰膝酸软，头昏眼花，失眠，多梦，遗精或血精，阳事易兴，排尿或大便时尿道有白浊滴出。舌红，少苔，脉细数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'31243187-8f25-457d-ac1d-e2b366b8b251', N'7934a3b3-43ff-4be4-9b63-b80f5789c3ae', N'082704', N'0827', N'肾阳虚损', 1, N'', N'头昏神疲，腰酸，膝冷，阳萎早泄，甚至稍劳后即尿道有白浊溢出。舌质淡胖，苔白，脉沉细。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'de3ccd5a-8039-4bb5-9fab-ff1180598e57', N'1bc5a4af-6342-431f-8b7d-00876f54693d', N'082801', N'0828', N'湿热下注', 1, N'', N'疮面色暗，或上附脓苔，脓水浸淫，秽臭难闻，四周漫肿灼热。伴有湿疹，痛痒时作，甚有恶寒发热。舌苔黄腻，脉数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3f2ff9cf-b6e3-4c6f-aa51-9b7306ee411e', N'1bc5a4af-6342-431f-8b7d-00876f54693d', N'082802', N'0828', N'脾虚湿盛', 1, N'', N'病程日久，疮面色暗，黄水浸淫，患肢浮肿，纳食腹胀，便溏，面色萎黄。舌淡，苔白腻，脉沉无力。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'13b5205f-0d87-426a-a052-3ed0b5bde45a', N'1bc5a4af-6342-431f-8b7d-00876f54693d', N'082803', N'0828', N'气虚血瘀', 1, N'', N'溃烂经年，腐肉已脱，起白色厚边，疮面肉色苍白，四周肤色黯黑，板滞木硬。舌质淡紫，苔白腻，脉细涩。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6a97990c-e643-4cab-aae4-6bc5999520dc', N'5ff2f19d-720d-4331-8870-8a76088b9c1c', N'082901', N'0829', N'气滞血瘀', 1, N'', N'局部皮肤出现褐色红斑，继而紫暗红肿，或有破损。苔脉随原发疾病而异。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c3c35665-c68a-4677-9e6d-ea6ec4e1985b', N'5ff2f19d-720d-4331-8870-8a76088b9c1c', N'082902', N'0829', N'蕴毒腐溃', 1, N'', N'褥疮溃烂，腐肉及脓水较多，或有恶臭，重者溃烂可深及筋骨，四周漫肿。伴有发热或低热，口苦且干，形神萎靡，不思饮食等。舌红，苔少，脉细数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'527c80e0-aded-4a51-911a-feafa1781313', N'5ff2f19d-720d-4331-8870-8a76088b9c1c', N'082903', N'0829', N'气血两虚', 1, N'', N'疮面腐肉难脱，或腐肉虽脱，新肌色淡，愈合缓慢。伴有面色huang(恍)白，神疲乏力，纳差食少等。舌质淡，苔少，脉沉细无力。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0b2a38b7-1239-4f6f-9c8b-20aa0b0b9574', N'33c518c4-703f-4052-8ebf-bdb9ae3e6d79', N'083001', N'0830', N'湿热瘀阻', 1, N'', N'除寒战高热等症状外，患肢疼痛彻骨，不能活动。继则局部胖肿，皮色不变，灼热，有明显的骨压痛和患肢叩击痛。苔黄，脉数（', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'dd86b593-dab5-449d-ac41-97babffbd4ec', N'33c518c4-703f-4052-8ebf-bdb9ae3e6d79', N'083002', N'0830', N'热毒炽盛', 1, N'', N'起病约1～2周后，高热持续不退。患肢胖肿，疼痛剧烈，皮肤掀红，灼热，内已酿脓。苔黄腻，脉洪数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'dfb9e7f0-8d49-43e3-a605-a1cabc1d5290', N'33c518c4-703f-4052-8ebf-bdb9ae3e6d79', N'083003', N'0830', N'脓毒蚀骨', 1, N'', N'溃后急性症状逐渐缓解，脓水淋漓，久则形成窦道。患肢肌肉萎缩，可摸到粗大之骨骼，以探针检查，常可触到粗糙死骨。可伴乏力，神疲，头昏，心悸，低热等症状。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8e18f0b4-6737-4869-ad6f-2aa4cd0668ea', N'4c9d8db8-1197-4c6c-9587-531ba0aed57e', N'083101', N'0831', N'阳虚痰凝', 1, N'', N'初起外形既不红热，又不肿胀，仅感病变关节隐隐酸痛。继则关节活动障碍，动则痛甚，全身情况无明显变化。舌淡，苔薄，脉濡细。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'33d08c97-8caa-46ba-8391-73edf2f12730', N'4c9d8db8-1197-4c6c-9587-531ba0aed57e', N'083102', N'0831', N'阴虚内热', 1, N'', N'数月后，在原发和继发部位渐渐漫肿，皮色微红，形成脓肿。伴有午后潮热，颧红，夜间盗汗，口燥咽干，食欲减退，或咳嗽痰血。舌红，少苔，脉细数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2ceb83f9-cc34-4184-929f-ad3ef4424b7c', N'4c9d8db8-1197-4c6c-9587-531ba0aed57e', N'083103', N'0831', N'肝肾亏虚', 1, N'', N'溃脓后疮口排出稀薄脓液，或夹有败絮样物，形成窦道。如病在四肢关节，患肢肌肉萎缩，畸形。病在颈、胸、腰椎者，则强直不遂，甚或下肢瘫痪不用，二便潴留或失禁。形体消瘦，面色huang(恍)白，畏寒，心悸，失眠，自汗盗汗。舌质淡红，苔白，脉细数或虚数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4a0322f8-e21c-4613-b7ca-4db1892af4cc', N'c947406e-6e96-44b9-bc7b-3205d076ca8c', N'083201', N'0832', N'寒湿阻络', 1, N'', N'患趾（指）喜暖怕冷，肤色苍白冰凉，麻木疼痛，遇冷痛剧。步履不利，多走则疼痛加剧，小腿酸胀，稍歇则痛缓（间歇性破行）。舌苔白腻，脉沉细，趺阳脉减弱或消失。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'91c76645-698c-4434-804d-67b75d402fa2', N'c947406e-6e96-44b9-bc7b-3205d076ca8c', N'083202', N'0832', N'血脉瘀阻', 1, N'', N'患趾（指）酸胀疼痛加重，步履沉重乏力，活动艰难。患趾（指）肤色由苍白转为暗红，下垂时更甚，抬高则见苍白。小腿可有游走性红斑、结节或硬索，疼痛持续加重，彻夜不能入寐。舌质暗红或有瘀斑，苔白，脉弦或涩。趺阳脉消失。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a81d2521-ba63-40c3-87d2-071980093dc5', N'c947406e-6e96-44b9-bc7b-3205d076ca8c', N'083203', N'0832', N'热毒伤阴', 1, N'', N'皮肤干燥，毫毛脱落，趾（指）甲增厚变形，肌肉萎缩，趾（指）多呈干性坏疽。舌红，苔黄，脉弦细数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'533217f8-5a73-4c06-acee-7783b7e5a090', N'c947406e-6e96-44b9-bc7b-3205d076ca8c', N'083204', N'0832', N'湿热毒盛', 1, N'', N'患肢剧痛，日轻夜重，喜凉怕热。局部皮色紫暗，肿胀，渐变紫黑，浸润蔓延，溃破腐烂，气秽，创面肉色不鲜，甚则五趾相传，波及足背，或伴有发热等症。舌红。苔黄腻，脉弦数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd9f9a5b6-b65c-45ad-b4dc-d03467718115', N'c947406e-6e96-44b9-bc7b-3205d076ca8c', N'083205', N'0832', N'气血两虚', 1, N'', N'面容憔悴，萎黄消瘦，神情倦怠。坏死组织脱落后疮面久不愈合，肉芽暗红或淡红而不鲜。舌质淡胖，脉细无力。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0ff90015-5cce-4260-962d-0b86f20ab7ba', N'c2d12a7f-7d98-4512-93b4-10b0e7a2f503', N'083301', N'0833', N'血热瘀结，相当于急性期，病变筋脉红肿热痛，上下游走，肢体活动不利，可有身热。舌红，苔黄，脉数。', 1, N'', N'', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9e396a54-d585-409b-96c9-25935a492430', N'c2d12a7f-7d98-4512-93b4-10b0e7a2f503', N'083302', N'0833', N'瘀阻脉络', 1, N'', N'相当于慢性期，病变筋脉肿如硬索，粘连不移，牵扯不适，或呈多个硬性结节，皮色褐黑，胫踝水肿。轻者舌脉无变化，重者舌质暗红或有瘀斑，脉多沉涩。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2d394b94-757d-4d0e-913c-7d0b92c9bb6e', N'09071624-e613-4732-ad5a-c7c75ba046a9', N'083401', N'0834', N'气滞血瘀', 1, N'', N'髂股静脉病变时，整个下肢肿胀疼痛，皮肤苍白或紫络，扪之烘热，腿胯部疼痛固定不移，全身发热。舌暗或有瘀斑，脉数。小腿深静脉病变时，腓肠肌胀痛，触痛，胫踝肿胀，行走困难，全身可有低热。苔白或腻，脉数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'46c9bddb-7c86-4738-a065-cb074cfa3a7a', N'09071624-e613-4732-ad5a-c7c75ba046a9', N'083402', N'0834', N'气虚血瘀', 1, N'', N'患肢肿胀久不消退，按之不硬而无明显凹陷，沉重麻木，皮肤发凉，颜色苍白，青筋显露，倦怠乏力。舌淡而有齿痕、瘀斑，苔薄白，脉沉而涩。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'65b57524-a3cb-489f-89f7-5a8aab34d027', N'3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', N'083501', N'0835', N'火盛伤阴', 1, N'', N'高热，烦躁，口干便秘，尿少而赤。舌质红而干，苔黄腻或黄糙，或舌光无苔，脉洪大弦数或弦细而数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'cafc5290-824b-476b-8a86-db5d011260c9', N'3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', N'083502', N'0835', N'火毒炽盛', 1, N'', N'创面腐烂，分泌物增多，局部水肿，寒战高热，汗多，口渴，小便短赤。舌质红，苔黄，脉洪数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'acb409fb-11a0-45fc-88db-bf0c2b4be54b', N'3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', N'083503', N'0835', N'火毒传心', 1, N'', N'兼见烦躁不宁，神昏，谵语，小便刺痛不爽。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1ea2b399-ebec-4997-bc79-df7e0f3b236a', N'3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', N'083504', N'0835', N'火毒传肺', 1, N'', N'兼见气粗喘息，咳嗽多痰，甚者咯血。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ba9aa7fa-3787-4583-9e54-a49fad3efe06', N'3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', N'083505', N'0835', N'火毒传肝', 1, N'', N'兼见痉挛抽搐或黄疸。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'df5ccb06-6894-44b3-b5e9-0e65d44b1989', N'3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', N'083506', N'0835', N'火毒传脾', 1, N'', N'兼见腹胀便秘或便溏而粘臭，恶心呕吐，不思饮食。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c6a6a6d0-7ae4-4caf-8144-5cef7ee90b62', N'3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', N'083507', N'0835', N'火毒传肾', 1, N'', N'兼见血尿，尿少，尿闭，浮肿。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ff145ed4-f8ad-4235-9008-26d852fc9d53', N'3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', N'083508', N'0835', N'阴损及阳', 1, N'', N'具有火盛伤阴见症外，兼见精神萎靡，气促，体温反低，肢冷或振颤。舌质淡嫩，脉虚大无力，重按无根，或微细。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'26de419e-8a6d-43d5-baca-950f5eb7ceee', N'3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', N'083509', N'0835', N'气血两虚', 1, N'', N'后期热毒渐退，而有神疲乏力，食欲不振，创面肉芽色淡不红，新肉难长。舌质淡嫩，苔薄白，脉虚数或迟缓。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'74e41b99-af98-48b0-ba47-0ba33fdc189a', N'3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', N'083510', N'0835', N'阴伤胃败', 1, N'', N'后期热毒渐退，口舌生糜，嗳气呕逆，口干少津，食欲日减，或兼有腹胀便泄。舌光如镜，质暗红而干，脉细数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'148bff37-885e-4d1c-8d26-7479e7427e15', N'f18b6b67-2a19-493f-ab62-98c5a9e7b510', N'083601', N'0836', N'阴盛阳衰', 1, N'', N'四肢厥逆，恶寒倦卧，极度疲乏，昏昏欲睡，呼吸微弱。苔白，脉沉微细。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0daa43ff-2c9a-4756-bbc6-da761522bd10', N'f18b6b67-2a19-493f-ab62-98c5a9e7b510', N'083602', N'0836', N'血虚寒凝', 1, N'', N'形寒肢冷，局部疼痛喜暖。舌淡而暗，苔白，脉沉细。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8cb43a9d-b200-4caa-abb3-632d530c1a2f', N'f18b6b67-2a19-493f-ab62-98c5a9e7b510', N'083603', N'0836', N'气血两虚', 1, N'', N'头晕目眩，少气懒言，四肢倦怠，面色苍白或萎黄，疮口不敛。舌质淡，苔白，脉细弱或虚大无力。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'de30e72c-b20a-41c6-9073-9bf2100226b6', N'f18b6b67-2a19-493f-ab62-98c5a9e7b510', N'083604', N'0836', N'瘀滞化热', 1, N'', N'发热口干，患处暗红微肿，疼痛喜冷。或患处红肿灼热，溃烂腐臭，脓水淋漓，筋骨暴露。舌暗红，苔黄，脉数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'11f2b601-0ca3-4c52-9231-6881600ada6d', N'7ceaa2cd-e61b-4199-a1d7-c901cebeb3ce', N'083701', N'0837', N'风毒（神经毒）', 1, N'', N'一般局部不红，不肿，不出血，疼痛轻微，感觉麻木，眼睑下垂，复视，表情肌麻痹，张口困难，言语不清，口角流涎，呼吸急促。脉沉伏迟弱。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0ff9d71f-1494-4709-a529-08f2ad6921dd', N'7ceaa2cd-e61b-4199-a1d7-c901cebeb3ce', N'083702', N'0837', N'火毒（血循毒）', 1, N'', N'伤口疼痛剧烈，出血，皮肤有血疮瘀斑，伤肢水肿明显。内脏、五官出血，发热，少尿无尿，心悸头晕。脉象细数或结代。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'81526254-6c34-4556-b054-10d2300a209a', N'7ceaa2cd-e61b-4199-a1d7-c901cebeb3ce', N'083703', N'0837', N'风火毒（混合证）', 1, N'', N'同时具有以上两种症状。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4782b635-36e7-4fd5-9ab1-27e7f62faad4', N'25c64bd9-9ef8-4554-8334-a76a8196f2c0', N'083801', N'0838', N'风毒在表', 1, N'', N'轻度吞咽困难和牙关紧闭，周身拘急，抽搐较轻，痉挛期短，间歇期较长。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'19ed5a00-8ff4-44be-b455-958ea28d7880', N'25c64bd9-9ef8-4554-8334-a76a8196f2c0', N'083802', N'0838', N'风毒入里', 1, N'', N'角弓反张，频繁而间歇期短的全身肌肉痉挛，高热，面色青紫，呼吸急促，痰涎壅盛，胸腹满闷，腹壁板硬，时时汗出，大便秘结，小便不通。舌质红绛，苔黄糙，脉弦数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e57bd174-9647-487c-981c-9523e9bfbbe7', N'83886669-4da0-48aa-9dde-c1169e25be3c', N'071703', N'0717', N'热毒炽盛', 1, N'', N'发病急骤，黄疸迅速加深，色黄如金。伴有高热烦渴，神昏谵语，或见衄血、便血、肌肤瘀斑。舌质红绛，苔黄而燥，脉弦滑数。', CAST(0x00009A7A01032AE0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'bb766686-e6d7-4615-b655-ecdc7ac37c9f', N'83886669-4da0-48aa-9dde-c1169e25be3c', N'071704', N'0717', N'寒凝阳衰', 1, N'', N'病程较长，身目俱黄，黄色晦暗。纳少脘闷，或腹胀便溏，神疲畏寒，口淡不渴。舌淡，苔白腻，脉濡缓或沉迟。', CAST(0x00009A7A01032AE0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9a1f8af7-7712-476c-b0fd-d8ce95ab6e38', N'bc25108c-8066-417d-bfac-30872064b1ab', N'071801', N'0718', N'气滞湿阻', 1, N'', N'腹胀按之不坚，胁下胀痛，饮食减少，食后作胀，嗳气不适，小便短少。舌苔白腻，脉弦。', CAST(0x00009A7A01032AE0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'240684d3-e95f-4e16-a605-16a117f4c31e', N'bc25108c-8066-417d-bfac-30872064b1ab', N'071802', N'0718', N'寒湿困脾', 1, N'', N'腹大胀满，按之如囊裹水，颜面微浮，下肢浮肿，脘腹痞胀，精神困倦，怯寒懒动，食少便溏，尿少。舌苔白滑或白腻，脉缓。', CAST(0x00009A7A01032AE0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b8f476b9-06f1-4ea5-a43f-3fc92a328ea2', N'bc25108c-8066-417d-bfac-30872064b1ab', N'071803', N'0718', N'湿热蕴结', 1, N'', N'腹大坚满，脘腹撑急，烦热口苦，渴不欲饮，小便短黄，大便秘结或溏垢，两目、皮肤发黄。舌边尖红，苔黄腻或灰黑，脉弦滑或数。', CAST(0x00009A7A01032AE0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6e9cd3e5-a60a-4eaf-b544-1e588c0f305c', N'bc25108c-8066-417d-bfac-30872064b1ab', N'071804', N'0718', N'肝脾血瘀', 1, N'', N'腹大坚满，脉络怒张，胁肋刺痛，面色黯黑，面颈胸臂有血痣，呈丝纹状，手掌赤痕，唇色紫褐，口渴不欲饮，大便色黑。舌质紫红或有瘀斑，脉细涩。', CAST(0x00009A7A01032AE0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'14c53a98-9505-491e-84d6-e2cd2dc1dd6d', N'bc25108c-8066-417d-bfac-30872064b1ab', N'071805', N'0718', N'脾肾阳虚', 1, N'', N'腹大胀满，早轻暮重，面色苍黄，脘闷纳呆，神倦怯寒，肢冷或下肢浮肿，食少便溏，小便短少不利。舌质淡紫，脉沉弦无力。', CAST(0x00009A7A01032AE0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'cd215883-bf99-4c73-a0ea-b450c8610ce5', N'bc25108c-8066-417d-bfac-30872064b1ab', N'071806', N'0718', N'肝肾阴虚', 1, N'', N'腹大胀急，或见青筋暴露，面色晦暗，唇紫口燥，心烦失眠，牙龈出血，鼻衄时作，小便短少。舌质红绛少津，脉弦细数。', CAST(0x00009A7A01032AE0 AS DateTime), N'', 3)
