INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'06fe6ac0-e877-4106-bbd3-21be657e617f', N'312f8471-2c4f-499d-bc06-6a68534ecdf5', N'076603', N'0766', N'阴虚火旺', 1, N'', N'寐中头、颈、胸背或全身出汗，汗出而醒，心烦身热，口渴咽干，唇红，午后潮热，颧红.舌质红，苔薄白，脉细数.', CAST(0x00009BD10147B358 AS DateTime), N'', 0)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ee003c95-aa14-497e-a6a1-703c9cf9b1d9', N'312f8471-2c4f-499d-bc06-6a68534ecdf5', N'076604', N'0766', N'气阴两虚', 1, N'', N'自汗、盗汗，畏寒，劳累后加重，神倦乏力，咽干口渴.舌红，苔薄白，脉细数.', CAST(0x00009BD10147B3EA AS DateTime), N'', 0)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'97cdc066-a6c5-4899-bd74-81ec61984f1a', N'6cf24bbf-75a3-4985-8dee-e4030f0be8d2', N'076703', N'0767', N'忧郁伤神', 1, N'', N'神志恍惚不安，心胸烦闷，多梦易醒，悲忧善哭.舌尖红苔薄白，脉弦细.', CAST(0x00009BD10147B5FB AS DateTime), N'', 0)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'16521895-e2d0-4e5a-be9f-52c4b278836b', N'6cf24bbf-75a3-4985-8dee-e4030f0be8d2', N'076705', N'0767', N'阴虚火旺', 1, N'', N'病久虚烦少寐，烦躁易怒，头晕心悸，颧红，手足心热，口干咽燥，或见盗汗.舌红，苔薄，脉弦细或细数.', CAST(0x00009BD10147B6EF AS DateTime), N'', 0)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5581fe37-2d8f-4c4d-a24d-ccf7df1c7c28', N'4108b40f-2393-451d-862f-afff4d47ce2f', N'076801', N'0768', N'痰气郁结', 1, N'', N'精神抑郁，神志呆钝，胸闷叹息，忧虑多疑，自语或不语，不思饮食.舌苔薄白而腻，脉弦细或弦滑.', CAST(0x00009BD10147B7B9 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'de58cb53-66d6-4a95-b955-a63f3afe4ebe', N'4108b40f-2393-451d-862f-afff4d47ce2f', N'076802', N'0768', N'气虚痰结', 1, N'', N'精神抑郁，淡漠少语，甚则目瞪若呆，妄闻妄见，面色萎黄，便溏溲清.舌质淡，舌体胖，苔白腻，脉滑或脉弱.', CAST(0x00009BD10147B829 AS DateTime), N'', 0)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'88988306-c4c7-4885-b47d-96680eb85553', N'4108b40f-2393-451d-862f-afff4d47ce2f', N'076803', N'0768', N'心脾两虚', 1, N'', N'神志恍惚，言语错乱，心悸易惊，善笑欲哭，夜寐不安，食少倦怠.舌质淡，苔白，脉细弱.', CAST(0x00009BD10147B89A AS DateTime), N'', 0)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'154ed7a1-af3b-4061-9f01-14d715ced069', N'4108b40f-2393-451d-862f-afff4d47ce2f', N'076804', N'0768', N'阴虚火旺', 1, N'', N'神志恍惚，多言善惊，心烦易躁，不寐，形瘦面红，口干.舌质红，苦少或无苔，脉细数.', CAST(0x00009BD10147B90F AS DateTime), N'', 0)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'bbb87701-d513-4cc9-a72c-191c4247c893', N'9c68fc3e-3a8e-4dc5-9b8b-b0691c870daf', N'076901', N'0769', N'痰火扰神', 1, N'', N'彻夜不眠，头痛躁狂，两目怒视，面红目赤，甚则狂乱莫制，骂人毁物，逾垣上屋，高歌狂呼.舌质红绛，苔多黄腻或黄燥，脉弦大滑数.', CAST(0x00009BD10147B9E7 AS DateTime), N'', 3)
