INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e1ceb5c1-f566-4b13-a4a8-76ddaf03d1ee', N'a072ea45-a851-4560-96c3-dcd8ccdfda7e', N'078101', N'0781', N'命门火衰', 1, N'', N'阳痿不举，面色huang(恍）白，头晕目眩，精神萎靡，腰膝酸软，畏寒肢冷，耳鸣.舌淡，苔白，脉沉细.', CAST(0x00009BD10147DC94 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'367beaf6-0e1f-43f7-af89-8fc6ae60e54a', N'8caf68a4-c1a7-4d04-a08f-b08bd48ff807', N'078202', N'0782', N'湿热下注', 1, N'', N'有梦遗精频作，，尿后有精液外流，小便短黄而混，或热涩不爽，口苦烦渴.舌红，苔黄腻，脉滑数.', CAST(0x00009BD10147DF12 AS DateTime), N'', 0)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'96689894-2b3b-4ea3-8916-3d5bf151aeb3', N'8caf68a4-c1a7-4d04-a08f-b08bd48ff807', N'078203', N'0782', N'心脾两虚', 1, N'', N'遗精遇思虑或劳累过度而作.头晕失眠，心悸健忘，面黄神倦，食少便溏.舌质淡，苔白，脉细弱.', CAST(0x00009BD10147DF95 AS DateTime), N'', 0)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'72007a0e-79a7-477e-a96b-da960057ee0b', N'8caf68a4-c1a7-4d04-a08f-b08bd48ff807', N'078204', N'0782', N'肾虚不固', 1, N'', N'遗精频作，甚则滑精.腰酸膝软，头晕目眩，耳鸣，健忘，心烦失眠.肾阴虚者，兼见颧红，盗汗，舌红，苔少，脉弦数；肾阳虚者，可见阳痿早泄，精冷，畏寒肢冷，面浮huang(恍）白，舌淡，苔白滑，尖边齿印，脉沉细.', CAST(0x00009BD10147E014 AS DateTime), N'', 0)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'14e2a3b1-e9b9-4901-8b32-f65950e26006', N'0a589dd1-7110-44c3-a67a-f0ec7bb2fa4a', N'078301', N'0783', N'行痹（风邪偏胜）', 1, N'', N'肢体关节肌肉疼痛，游走不定，屈伸不利，或见恶风发热等.舌苔薄白，脉浮.', CAST(0x00009BD10147E0DE AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e35f619a-2da3-4d22-903c-76ae17c7e9f7', N'0a589dd1-7110-44c3-a67a-f0ec7bb2fa4a', N'078304', N'0783', N'热痹（热邪偏胜）', 1, N'', N'起病急骤，关节疼痛，局部红肿灼热，痛不可触，屈伸不利，得冷稍舒.多有发热恶风，多汗，心烦口渴.舌红苔黄，脉滑数.', CAST(0x00009BD10147E262 AS DateTime), N'', 0)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'866f7748-9265-4ccb-bcf3-b78bcda9f014', N'ba71291f-1732-4dc8-ae0a-3a82425bd54f', N'078401', N'0784', N'风寒湿阻', 1, N'', N'关节肿胀疼痛，痛有定处，晨僵屈伸不利，遇寒则痛剧，局部畏寒伯冷.舌苔薄白，脉浮紧或沉紧.', CAST(0x00009BD10147E41B AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'44917c59-3b47-478e-b3a5-b0c45b04a21f', N'ba71291f-1732-4dc8-ae0a-3a82425bd54f', N'078403', N'0784', N'痰瘀互结', 1, N'', N'关节漫肿日久，僵硬变形，屈伸受限，疼痛固定，痛如锥刺，昼轻夜重，口干不欲饮.舌质紫暗，苔白腻或黄腻，脉细涩或细滑.', CAST(0x00009BD10147E550 AS DateTime), N'', 0)
